mapper.select:
select 

from iot_sys_permission
where 1=1

   and  name like concat('%',#{name},'%')


   and  parentid =#{parentid}


   and  type =#{type}


   and  available =#{available}

select distinct menu.* from iot_sys_role_permission rm left join iot_sys_permission menu on menu.id=rm.sys_permission_id left join iot_sys_role r on rm.sys_role_id=r.id where 1=1
and r.id=#{id}
and menu.available=1 order by menu.sortstring asc

mapper.insert:
insert into iot_sys_permission (id, name, type, 
  url, percode, parentid, 
  parentids, sortstring, available
  )
values (#{id,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}, 
  #{url,jdbcType=VARCHAR}, #{percode,jdbcType=VARCHAR}, #{parentid,jdbcType=VARCHAR}, 
  #{parentids,jdbcType=VARCHAR}, #{sortstring,jdbcType=VARCHAR}, #{available,jdbcType=CHAR}
  )
