mapper.select:

SELECT t1. sys_role_id id,t1.sys_user_id,t2.name FROM iot_sys_user_role
t1,iot_sys_role t2 WHERE t1.sys_role_id=t2.id AND t2.available=1
AND t1.sys_user_id=#{id}

mapper.insert:
insert into iot_sys_role (id, name, available
)
values (#{id,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR},
#{available,jdbcType=CHAR}
)

mapper.update:
update iot_sys_role
set name = #{name,jdbcType=VARCHAR},
available = #{available,jdbcType=CHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
SELECT t1.id,sys_user_id,sys_role_id,t2.name FROM iot_sys_user_role
t1,iot_sys_role t2 WHERE t1.sys_role_id=t2.id AND t2.available=1 AND
sys_user_id=#{userId}

